(()=>{document.getElementById("display");const e=document.getElementById("search-btn"),t=document.getElementById("city-search"),n=document.getElementById("search-error");e.onclick=function(){!async function(e){try{const c=`https://api.weatherapi.com/v1/forecast.json?key=1fdeb95185c145f0809201723232908&q=${e}&days=3&aqi=yes&alerts=no`,o=await fetch(c,{mode:"cors"});o.ok||(n.textContent=`City "${e}" not found. Check search and try again`);const[r,i]=[{country:(t=await o.json()).location.country,city:t.location.name,date:t.location.localtime.slice(-5),timeZone:t.location.localtime.slice(0,10)},{condition:t.current.condition.text,conditionIcon:t.current.condition.icon,clouds:t.current.cloud,precip:t.current.precip_mm,temp:t.current.temp_c,tempFeels:t.current.feelslike_c,wind:t.current.wind_kph,windGust:t.current.gust_kph,windDir:t.current.wind_dir}];return[r,i]}catch(e){return alert(e),null}var t}(document.getElementById("city-search").value)},t.addEventListener("keypress",(t=>{"Enter"===t.key&&e.click()}))})();